import React,{Component} from "react";
import { Link } from "react-router-dom";
import axios from 'axios';

const Ride = props => (
    <tr>
        <td>{props.rides.group_Name}</td>
        <td>{props.rides.host_email}</td>
        <td>{props.rides.location}</td>
        <td>{props.rides.date.substring(0,10)+ " "+
            props.rides.date.substring(11,16)}</td>
        <td>{props.rides.seats}</td>
        {/* <td>
            <Link to={"/edit/"+props.exercise._id}>edit</Link> |
            <a href="#" onClick={()=>{props.deleteExercise(props.exercise._id)}}>delete</a>
        </td> */}
    </tr>
)

export default class RidesList extends Component{
    constructor(props){
        super(props);

        // this.deleteExercise = this.deleteExercise.bind(this);

        this.state = {rides:[]};
    }

    componentDidMount(){
        axios.get('/rides')
            .then(response =>{
                console.log(response.data);
                
                this.setState({rides: response.data})
            })
            .catch((error)=> console.log(error));
    }

    // deleteExercise(id){
    //     axios.delete('http://localhost:5000/exercises/' + id)
    //         .then(res => console.log(res.data));

    //     this.setState({
    //         exercises: this.state.exercises.filter(el => el._id !==id)
    //     })
    // }

    rideList(){
        return this.state.rides.map(currRide =>{
            // return <Ride rides = {currRide} deleteExercise= {this.deleteExercise} key={currExercise._id}/>;
            return <Ride rides = {currRide} key={currRide._id}/>;

        })
    }

    render(){
        return(
          <div>
              <Link to="/">Back to Home</Link>
              <h3>Rides Available</h3>
              <table className="table">
                <thead className="thead-light">
                    <tr>
                        <th>Group Name</th>
                        <th>Host Email</th>
                        <th>Location</th>
                        <th>Date</th>
                        <th>Seats Available</th>
                    </tr>
                </thead>
                <tbody>
                    {this.rideList()}
                </tbody>
              </table>
              
          </div>
          
        )
    }
}
