import React,{Component} from "react";
import { Link } from "react-router-dom";
import axios from 'axios';
import PropTypes from "prop-types";
import { connect } from "react-redux";



const Ride = props => (
    <tr>
        <td>{props.rider.rider_email}</td>
        <td>{props.rides.rider}</td>
    </tr>
)

class RidesHost extends Component{
    constructor(props){
        super(props);
        const {user} = this.props.auth;

        this.state = {
                        rides:[],
                        rider:[],
                        user:user,
                    };
    }

    componentDidMount(){
        const id = this.props.match.params.id;
        axios.get('/rides/'+id)
            .then(response =>this.setState({
                rides: response.data,
                rider: response.data.rider
            }))
            .catch((error)=> console.log(error));
                    
    }

    riderList(){
        return this.state.rider.map(currRider =>{
            return <Ride rider = {currRider} key={currRider._id}/>;

        })
    }


    render(){
        const date = this.state.date;
        console.log(date);
        
        return(
          <div>
                <Link to="/">Back to Home</Link>3
                <h1>{this.state.rides.group_Name}</h1>
                <h3>Location: </h3>
                <p>{this.state.rides.location}</p>
                <h3>Departure Time:</h3>
                <p>{date}</p>
                <p>{this.state.rides.time}</p>

          </div>
          
        )
    }
}

RidesHost.propTypes = {
    auth:PropTypes.object.isRequired
}

const mapStateToProps = state =>({
    auth:state.auth,
});

export default connect(
    mapStateToProps,
)(RidesHost);
