import React,{Component} from 'react';
import {
    GoogleMap,
    Marker,
    InfoWindow,
    LoadScript,
}from "@react-google-maps/api"


const mapContainerStyle = {
    height: "100vh",
    width: "100vw",
}

const center = {
    lat:37.774929,
    lng:-122.419418,
}

const options = {
    disableDefaultUI:true,
    zoomControl:true,
}



class RideMap extends Component{
    constructor(props){
        super(props);
        this.setMarker = this.setMarker.bind(this);

        this.state = {
                        markers:[],
                    };
    }

    setMarker(e){
        this.setState({
            markers:[...this.state.markers,{
                        lat:e.latLng.lat(),
                        lat:e.latLng.lng(),
                        time: new Date()}]
        })
        console.log(e);
    }

    render(){
        
        return(
            <div>
                <LoadScript 
                    googleMapsApiKey={process.env.REACT_APP_GOOGLE_KEY}
                >
                    <h2>Rides</h2>
                    <GoogleMap mapContainerStyle={mapContainerStyle} 
                        zoom={8}
                        center={center}
                        options={options}
                        onClick={event=>this.setMarker(event)}
                    >
                        {this.state.markers.map(marker => <Marker key={marker.time.toISOString()} position={{lat:marker.lat,lng:marker.lng}}/>)}
                    </GoogleMap>
                </LoadScript>
            </div>
           
            
        )
    }
}



export default RideMap;
