import React,{Component} from "react";
import { Link } from "react-router-dom";
import axios from 'axios';
import PropTypes from "prop-types";
import { connect } from "react-redux";



const Rider = props => (
    <div className="row">
        <h5  className="col-md-2"><small>{props.rider.rider}</small></h5>
        <h5 className="col-sm-3"><small>{props.rider.rider_email}</small></h5>
    </div>
)



class RidesHost extends Component{
    constructor(props){
        super(props);
        const {user} = this.props.auth;

        this.state = {
                        rides:[],
                        rider:[],
                        user:user,
                    };
    }

    componentDidMount(){
        const id = this.props.match.params.id;
        axios.get('/rides/view/'+id)
            .then(response =>{
                this.setState(
                {
                    rides: response.data,
                    rider: response.data.rider
                })
                
            })
            .catch((error)=> console.log(error));
                    
    }

    riderList(){
        return this.state.rider.map(currRider =>{
            return <Rider rider = {currRider} key={currRider._id}/>;

        })
    }


    render(){
        console.log(this.state.rider);
        
        return(

                <div className="container"  style={{minHeight:"00px"}}>
                    <div className="card mb4 box-shadow">
                    <Link to="/">Back to Home</Link>
                    <h2>{this.state.rides.group_Name}</h2>
                    <div className="row">
                        <h5 className="col-md-1">Location: </h5>
                        <h5 className="col-md-8">{this.state.rides.location}</h5>
                    </div>
                    
                    <div className="row">
                        <h5 className="col-md-2">Departure Date:</h5>
                        <h5 className="col-md-3">{this.state.rides.date?this.state.rides.date.substring(0,10):""}
                            {this.state.rides.time}
                        </h5>
                        
                    </div>
                    <br/>
                    <br/>
                    <br/>
                    <h4>Riders</h4>
                    {this.riderList()}
                    </div>
                </div>
          
          
          
        )
    }
}

RidesHost.propTypes = {
    auth:PropTypes.object.isRequired
}

const mapStateToProps = state =>({
    auth:state.auth,
});

export default connect(
    mapStateToProps,
)(RidesHost);
