import React,{Component,useRef} from 'react';
import {
    GoogleMap,
    Marker,
    InfoWindow,
    LoadScript,
}from "@react-google-maps/api";

import usePlacesAutocomplete,{
    getGeocode,
    getLatLng,
} from "use-places-autocomplete";

import {
    Combobox,
    ComboboxInput,
    ComboboxPopover,
    ComboboxList,
    ComboboxOption,
  } from "@reach/combobox";
  import "@reach/combobox/styles.css";
import Search from 'antd/lib/input/Search';

const mapContainerStyle = {
    height: "100vh",
    width: "100vw",
}

const center = {
    lat:37.774929,
    lng:-122.419418,
}

const options = {
    disableDefaultUI:true,
    zoomControl:true,
}

const libraries = ["places"];


const PlacesAutocomplete  = () =>{
    const{
        ready,
        value,
        suggestions:{status,data},
        setValue,
        clearSuggestion,
    } = usePlacesAutocomplete({
        requestOptions:{
            location:{ lat:()=>37.774929,lng:()=>-122.419418},
            radius:200*1000,
        },
    });
    return (
        <div className='search'>
            <Combobox onSelect={ async (address)=>{
                try{
                    const results = await getGeocode({address});
                    const {lat, lng} = await getLatLng(results[0]);
                    console.log(lat,lng);
                }catch(err){
                    console.log("errors!");
                }
                console.log(address);
                
                }}>
                <ComboboxInput 
                    value={value} 
                    onChange={(e)=>{setValue(e.target.value);}} 
                    disabled={!ready}
                    placeholder="Enter an address"
                    />
                    <ComboboxPopover>
                        {status==="OK" && data.map(({id,description})=>(
                            <ComboboxOption key={id} value={description}/>
                        ))}
                    </ComboboxPopover>
            </Combobox>
        </div>
        
    )
}
const mapRef = useRef();
function onMapLoad(map){
    this.setState({
        mapRef:map
    } )
}

class RideMap extends Component{
    constructor(props){
        super(props);
        this.setMarker = this.setMarker.bind(this);
        this.state = {
                        markers:[],
                        selected:null,
                        
                    };
    }

    setMarker(e){
        const lat = e.latLng.lat();
        const lng = e.latLng.lng();
        this.setState({
            markers:[...this.state.markers,{
                        lat:lat,
                        lng:lng,
                        time: new Date()}]
        })
        
    }
   
    render(){
        
        return(
            <div>
                <LoadScript 
                    googleMapsApiKey={process.env.REACT_APP_GOOGLE_KEY}
                    libraries={libraries}
                >
                    <PlacesAutocomplete />
                    <h2>Rides</h2>
                    <GoogleMap mapContainerStyle={mapContainerStyle} 
                        zoom={8}
                        center={center}
                        options={options}
                        onClick={event=>this.setMarker(event)}
                    >
                        {this.state.markers.map(marker => 
                            <Marker key={marker.time.toISOString()} 
                                    position={{lat:marker.lat,lng:marker.lng}}
                                    onClick={()=>this.setState({selected:marker})}
                                    />)}
                        {this.state.selected? 
                        (<InfoWindow 
                            position={{lat:this.state.selected.lat,lng:this.state.selected.lng}} 
                            onCloseClick={this.setState({selected:null})}>
                            <div>
                                <h3>Rides Here</h3>
                            </div>
                        </InfoWindow>):null}
                    </GoogleMap>
                </LoadScript>
            </div>
           
            
        )
    }
}




export default RideMap;
