import React,{Component} from "react";
import { Link } from "react-router-dom";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { loginUser } from "../actions/authActions";
import { logoutUser } from "../actions/authActions";

class Navbar extends Component{

    onLogoutClick = e =>{
        e.preventDefault();
        this.props.logoutUser();
    };

    constructor(){
        super();
    }
    render(){
        return(
            isLoggedIn = this.props.auth.isAuthenticated,
            <nav className="navbar navbar-dark bg-dark navbar-expand-lg">
                <Link to="/" className="navbar-brand">ExcerTracker</Link>
                <div className = "collpase navbar-collapse">
                    <ul className = "navbar-nav mr-auto">
                        <li className="navbar-item">
                            <Link to="/" className="nav-link">Exercise</Link>
                        </li>

                        <li className="navbar-item">
                            <Link to="/create" className="nav-link">Create Exercise </Link>
                        </li>

                        <li className="navbar-item">
                            <Link to="/user" className="nav-link">Create user</Link>
                        </li>
                    </ul>

                    <ul className = "navbar-nav navbar-right">
                      
                        <li>
                            {this.isLoggedIn?
                                <button type="button" 
                                onClick={this.onLogoutClick}
                                class="btn btn-danger btn-small btn-nav navbar-right">Log out</button>:
                                <button type="button" 
                                class="btn btn-primary btn-small btn-nav navbar-right">Log In</button>
                            }
                        </li>
                    </ul>
                </div>
            </nav>
        );
    }
} 

Navbar.propTypes = {
    loginUser:PropTypes.func.isRequired,
    logoutUser: PropTypes.func.isRequired,
    auth:PropTypes.object.isRequired,
}

const mapStateToProps = state =>({
    auth:state.auth,
});

export default connect(
    mapStateToProps,
    { logoutUser }
)(Navbar);
