import React,{Component} from "react";
import { Link } from "react-router-dom";
import axios from 'axios';
import PropTypes from "prop-types";
import { connect } from "react-redux";

const Ride = props => (
    <tr>
        <td>{props.rides.group_Name}</td>
        <td>{props.rides.host_email}</td>
        <td>{props.rides.location}</td>
        <td>{props.rides.date.substring(0,10)+ " "+
            props.rides.time}</td>
        <td>{props.rides.seats}</td>
        <td>
            <button className="btn btn-primary btn-small mr-1" onClick={()=>{props.joinRide(props.rides._id)}}>Join</button>
            
<button className="btn btn-danger btn-small mr-1" onClick={()=>{props.deleteRides(props.rides._id)}}>delete{props.user.email}</button>
        </td>
    </tr>
)
class RidesList extends Component{
    constructor(props){
        super(props);

        this.deleteRides = this.deleteRides.bind(this);
        this.joinRide = this.joinRide.bind(this);
        const {user} = this.props.auth;
        this.state = {
                        rides:[],
                        user:user
                    };
    }

    componentDidMount(){
        axios.get('/rides')
            .then(response =>{                
                this.setState({rides: response.data.filter(rl =>rl.seats>0)}) 
            })
            .catch((error)=> console.log(error));
    }

    deleteRides(id){
        
        
        axios.delete('/rides/'+id,{data:{host_email:this.user.email}})
            .then(res => console.log(res.data))
            .catch(err => console.log(err));

        this.setState({
            rides: this.state.rides.filter(rl => rl._id !==id)
        })
    }

    joinRide(id){        
        if(!this.props.auth.isAuthenticated){
            window.location = '/login';
        }else{
            const rider = {
                rider: this.user.username,
                rider_email: this.user.email
            }
            axios.post('/rides/join/'+id,rider)
                .then(res => {
                    window.alert(res.data);
                    window.location = '/rides';
                })
                .catch(err => window.alert(err.response.data));
    
        }


    }

    rideList(){
        user=this.user
        return this.state.rides.map(currRide =>{
            return <Ride rides = {currRide} deleteRides= {this.deleteRides} user={this.user} joinRide = {this.joinRide} key={currRide._id}/>;

        })
    }

    render(){
        return(
          <div>
              <Link to="/">Back to Home</Link>
              <h3>Rides Available</h3>
              <table className="table">
                <thead className="thead-light">
                    <tr>
                        <th>Group Name</th>
                        <th>Host Email</th>
                        <th>Location</th>
                        <th>Date</th>
                        <th>Seats Available</th>
                        <th></th>
                    </tr>
                </thead>
                <tbody>
                    {this.rideList()}
                </tbody>
              </table>
              
          </div>
          
        )
    }
}

RidesList.propTypes = {
    auth:PropTypes.object.isRequired,
}

const mapStateToProps = state =>({
    auth:state.auth,
});

export default connect(
    mapStateToProps,
)(RidesList);
