import React,{Component} from 'react';
import axios from 'axios';

import {
    GoogleMap,
    Marker,
    InfoWindow,
    LoadScript,
}from "@react-google-maps/api";

import usePlacesAutocomplete,{
    getGeocode,
    getLatLng,
} from "use-places-autocomplete";

import {
    Combobox,
    ComboboxInput,
    ComboboxPopover,
    ComboboxList,
    ComboboxOption,
  } from "@reach/combobox";
  import "@reach/combobox/styles.css";


import compass from "../../img/compass.svg";
const mapContainerStyle = {
    height: "100vh",
    width: "100vw",
}

const center = {
    lat:37.774929,
    lng:-122.419418,
}

const options = {
    disableDefaultUI:true,
    zoomControl:true,
}

const libraries = ["places"];

// const PlacesAutocomplete  = props =>{
//     const{
//         ready,
//         value,
//         suggestions:{status,data},
//         setValue,
//         clearSuggestions,
//     } = usePlacesAutocomplete({
//         requestOptions:{
//             location:{ lat:()=>37.774929,lng:()=>-122.419418},
//             radius:200*1000,
//         },
//     });
//     return (
//         <div className='search'>
//             <Combobox onSelect={ async (address)=>{
//                 setValue(address,false);
//                 clearSuggestions();

//                 try{
//                     const results = await getGeocode({address});
//                     const {lat, lng} = await getLatLng(results[0]);
//                     props.panTo({lat,lng});
//                 }catch(err){
//                     console.log(err);
//                 }
                
//                 }}>
//                 <ComboboxInput 
//                     value={value} 
//                     onChange={(e)=>{setValue(e.target.value);}} 
//                     disabled={!ready}
//                     placeholder="Enter an address"
//                     />
//                     <ComboboxPopover>
//                         <ComboboxList>
//                         {status==="OK" && data.map(({id,description})=>(
//                             <ComboboxOption key={id} value={description}/>
//                         ))}
//                        </ComboboxList>
//                     </ComboboxPopover>
//             </Combobox>
//         </div>
        
//     )
// }


const Locate  = props =>{
   return <button className="locate" onClick={()=>{
       navigator.geolocation.getCurrentPosition((
            position)=>{
                props.panTo({
                    lat: position.coords.latitude,
                    lng:position.coords.longitude
                });
            }
            ,()=>null
        );
   }}>
            <img src={compass} alt="compass - locate me" style={{width:"40px", height:"40px"}}></img>
          </button>
}
class RideMap extends Component{
    
    constructor(props){
        super(props);
        this.setMarker = this.setMarker.bind(this);
        this.onMapLoad = this.onMapLoad.bind(this);
        this.panTo = this.panTo.bind(this);
        this.state = {
                        markers:[],
                        selected:null,
                        map:{},
                        rides:[],
                    };
    }

    // componentDidMount(){
    //     axios.get('/api/rides')
    //         .then(response =>{this.setState({rides: response.data});
    //         })
    //         .catch((error)=> console.log(error));
    // }

    // onMapLoad(map){
    //     this.setState({
    //         map:map
    //     })
    // }

    setMarker(e){
        const lat = e.latLng.lat();
        const lng = e.latLng.lng();
        
        this.setState({
            markers:[...this.state.markers,{
                        lat:lat,
                        lng:lng,
                        time: new Date()}]
        })
        
    }

   panTo({lat,lng}){
        this.state.map.panTo({lat,lng});
        this.state.map.setZoom(14);
   }
    render(){
        
        return(
            <div>
                <LoadScript 
                    googleMapsApiKey={process.env.REACT_APP_GOOGLE_KEY}
                    libraries={libraries}
                >
                    {/* <PlacesAutocomplete panTo={this.panTo}/> */}
                    <Locate panTo={this.panTo}/>

                    <h2>Rides</h2>
                    <GoogleMap mapContainerStyle={mapContainerStyle} 
                        zoom={8}
                        center={center}
                        options={options}
                        onClick={event=>this.setMarker(event)}
                        onLoad={this.onMapLoad}
                    >
                        {this.state.markers.map(marker => 
                            <Marker key={marker.time.toISOString()} 
                                    position={{lat:marker.lat,lng:marker.lng}}
                                    onClick={()=>this.setState({selected:marker})}
                                    />)}
                        {this.state.selected? 
                        (<InfoWindow 
                            position={{lat:this.state.selected.lat,lng:this.state.selected.lng}} 
                            onCloseClick={this.setState({selected:null})}>
                            <div>
                                <h3>Rides Here</h3>
                            </div>
                        </InfoWindow>):null}
                    </GoogleMap>
                </LoadScript>
            </div>
           
            
        )
    }
}




export default RideMap;
