import React,{Component} from "react";
import { Link } from "react-router-dom";
import axios from 'axios';
import PropTypes from "prop-types";
import { connect } from "react-redux";

const Ride = props => (
    <tr>
        <td>{props.rides.group_Name}</td>
        <td>{props.rides.host_email}</td>
        <td>{props.rides.location}</td>
        <td>{props.rides.date.substring(0,10)+ " "+
            props.rides.time}</td>
        <td>{props.rides.seats}</td>
    </tr>
)
class MyRidesList extends Component{
    constructor(props){
        super(props);


        this.state = {
                        rides:[],
                        user:"",
                    };
    }

    componentDidMount(){
        const {user} = this.props.auth;
        console.log(user.email);
        
        axios.get('/rides/myRides',{rider_email:user.email})
            .then(response =>{                
                this.setState({rides: response.data,user:user})
            })
            .catch((error)=> console.log(error));
    }

    rideList(){
        return this.state.rides.map(currRide =>{
            return <Ride rides = {currRide} key={currRide._id}/>;

        })
    }

    render(){
        return(
          <div>
              <Link to="/">Back to Home</Link>
              <h3>Your Rides</h3>
              <table className="table">
                <thead className="thead-light">
                    <tr>
                        <th>Group Name</th>
                        <th>Host Email</th>
                        <th>Location</th>
                        <th>Date</th>
                        <th>Seats Available</th>
                        <th></th>
                    </tr>
                </thead>
                <tbody>
                    {this.rideList()}
                </tbody>
              </table>
              
          </div>
          
        )
    }
}

MyRidesList.propTypes = {
    auth:PropTypes.object.isRequired,
}

const mapStateToProps = state =>({
    auth:state.auth,
});

export default connect(
    mapStateToProps,
)(MyRidesList);
