import React,{Component} from "react";
import { Link } from "react-router-dom";
import axios from 'axios';
import PropTypes from "prop-types";
import { connect } from "react-redux";

class RidesList extends Component{
    constructor(props){
        super(props);

        this.deleteRides = this.deleteRides.bind(this);
        this.joinRide = this.joinRide.bind(this);
        const {user} = this.props.auth;
        
        this.state = {
                        rides:[],
                        user:user
                    };
    }

    componentDidMount(){
        axios.get('/rides')
            .then(response =>this.setState({rides: response.data}))
            .catch((error)=> console.log(error));
    }

    deleteRides(id){
        
        
        axios.delete('/rides/'+id,{data:{host_email:this.state.user.email}})
            .then(res => console.log(res.data))
            .catch(err => console.log(err));

        this.setState({
            rides: this.state.rides.filter(rl => rl._id !==id)
        })
    }

    joinRide(id){        
        if(!this.props.auth.isAuthenticated){
            window.location = '/login';
        }else{
            const rider = {
                rider: this.state.user.username,
                rider_email: this.state.user.email
            }
            axios.post('/rides/join/'+id,rider)
                .then(res => {
                    window.alert(res.data);
                    window.location = '/rides';
                })
                .catch(err => window.alert(err.response.data));
    
        }


    }

    rideList(){
        var rides = [];
        for(var i=0;i<this.state.rides.length;i++){
            rides.push(
                <tr>
                    <td>{this.state.rides[i].group_Name}</td>
                    <td>{this.state.rides[i].host_email}</td>
                    <td>{this.state.rides[i].location}</td>
                    <td>{this.state.rides[i].date.substring(0,10)+ " "+
                        this.state.rides[i].time}</td>
                    <td>{this.state.rides[i].seats}</td>
                    <td>
                        <button 
                            className="btn btn-primary btn-small mr-1" 
                            onClick={()=>{props.joinRide(this.state.rides[i]._id)}}>Join</button>
            
                        {props.user.email===this.state.rides[i].host_email?
                        <button className="btn btn-danger btn-small mr-1" 
                            onClick={()=>{props.deleteRides(this.state.rides[i]._id)}}>delete</button>: ""
                        }
                    </td>
                </tr>
            );
        }
        return rides;
    }

    render(){
        return(
          <div>
              <Link to="/">Back to Home</Link>
              <h3>Rides Available</h3>
              <table className="table">
                <thead className="thead-light">
                    <tr>
                        <th>Group Name</th>
                        <th>Host Email</th>
                        <th>Location</th>
                        <th>Date</th>
                        <th>Seats Available</th>
                        <th></th>
                    </tr>
                </thead>
                <tbody>
                    {this.rideList()}
                    

                </tbody>
              </table>
              
          </div>
          
        )
    }
}

RidesList.propTypes = {
    auth:PropTypes.object.isRequired,
}

const mapStateToProps = state =>({
    auth:state.auth,
});

export default connect(
    mapStateToProps,
)(RidesList);
