import React,{Component} from "react";
import { Link } from "react-router-dom";
import axios from 'axios';
import PropTypes from "prop-types";
import { connect } from "react-redux";
import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";


class AddRide extends Component{
    constructor(props){
        super(props);

        this.onChangeGroupName = this.onChangeGroupName.bind(this);
        this.onChangeSeats = this.onChangeSeats.bind(this);
        this.onChangeLocation = this.onChangeLocation.bind(this);
        this.onChangeDate = this.onChangeDate.bind(this);
        this.onChangeTime = this.onChangeTime.bind(this);
        this.onSubmit = this.onSubmit.bind(this);

        this.state = {
            seats:0,
            group_Name: '',
            location: '',
            date: new Date,
            time:'',
        };
    }

    
    onChangeSeats(e){
        this.setState({
            seats: e.target.value
        });
    }
    onChangeGroupName(e){
        this.setState({
            group_Name: e.target.value
        });
    }

    onChangeLocation(e){
        this.setState({
            location: e.target.value
        });
    }

    onChangeDate(date){
        this.setState({
            date: date
        });
    }

    onChangeTime(e){
        this.setState({
            time: e.target.value
        });
    }

    onSubmit(e){
        e.preventDefault();
        const {user} = this.props.auth;

        const ride = {
            host_email: this.user.email,
            group_Name: this.state.group_Name,
            location: this.state.location,
            date: this.state.date,
            time: this.state.time
        }

        axios.post('/rides/add',ride)
        .then(res =>console.log(res.data));
        console.log(ride);
        window.location = '/';
    }


    render(){
        return(
            <div>
            <h3>You are on the Create Exercises Component!</h3>
            <form onSubmit={this.onSubmit}>

                <div className="form-group">
                    <label>Group Name: </label>
                    <input 
                      type = "text"
                      className="form-control"
                      value = {this.state.group_Name}
                      onChange={this.onChangeGroupName}
                    />
                </div>


                <div className="form-group">
                    <label>Seats: </label>
                    <input 
                      type = "text"
                      className="form-control"
                      value = {this.state.seats}
                      onChange={this.onChangeSeats}
                    />
                </div>

                <div className="form-group">
                    <label>location: </label>
                    <input 
                      type = "text"
                      className="form-control"
                      value = {this.state.location}
                      onChange={this.onChangeLocation}
                    />
                </div>


                <div className="form-group">
                    <label>Date: </label>
                    <div>
                      <DatePicker
                          selected={this.state.date}
                          onChange={this.onChangeDate} 
                      />
                    </div>
                    
                </div>

                <div className="form-group">
                    <label>Time: </label>
                    <input 
                      type = "text"
                      className="form-control"
                      value = {this.state.time}
                      onChange={this.onChangeTime}
                    />
                </div>

                <div className="form-group">
                      <input type="submit" value="Create Ride" className="btn btn-primary"/>
                </div>

            </form>
        </div>
          
        )
    }
}

AddRide.propTypes = {
    auth:PropTypes.object.isRequired,
}

const mapStateToProps = state =>({
    auth:state.auth,
});

export default connect(
    mapStateToProps,
)(AddRide);
