import React,{Component} from "react";
import { Link } from "react-router-dom";
import axios from 'axios';
import PropTypes from "prop-types";
import { connect } from "react-redux";

const Ride = props => (
    <tr>
        <td><Link to ={"/rides/view/"+props.rides._id}>{props.rides.group_Name}</Link></td>
        <td>{props.rides.host_email}</td>
        <td>{props.rides.location}</td>
        <td>{props.rides.date.substring(0,10)+ " "+
            props.rides.time}</td>
        <td>{props.rides.seats}</td>
        <td>{props.user.email===props.rides.host_email? "Yes": "No"}</td>
        <td>
            {props.user.email===props.rides.host_email?
                <button className="btn btn-danger btn-small mr-1" 
                    onClick={()=>{props.deleteRides(props.rides._id)}}>delete</button>: ""}
        </td>
    </tr>
)
class MyRidesList extends Component{
    constructor(props){
        super(props);
        const {user} = this.props.auth;
        this.deleteRides = this.deleteRides.bind(this);
        this.deleteRides = this.deleteRides.bind(this);

        this.state = {
                        rides:[],
                        user:user,
                    };
    }

    componentDidMount(){
        const {user} = this.props.auth;
       
        axios.get('/rides/')
            .then(response =>{                
                this.setState({
                    rides: response.data
                })
            })
            .catch((error)=> console.log(error));
    }

    deleteRides(id){
        
        
        axios.delete('/rides/'+id,{data:{host_email:this.state.user.email}})
            .then(res => console.log(res.data))
            .catch(err => console.log(err));

        this.setState({
            rides: this.state.rides.filter(rl => rl._id !==id)
        })
    }

    rideList(){
        return this.state.rides.map(currRide =>{
            return <Ride rides = {currRide} user={this.state.user} deleteRides= {this.deleteRides} key={currRide._id}/>;

        })
    }

    filterList(){
        this.setState({
            rides:this.state.rides.filter(rl =>rl.rider.some(riders =>riders.rider_email===this.state.user.email))
        })
    }

    render(){
        
        return(
          <div>
              <Link to="/">Back to Home</Link>
              <h3>Your Rides</h3>
              <table className="table">
                <thead className="thead-light">
                    <tr>
                        <th>Group Name</th>
                        <th>Host Email</th>
                        <th>Location</th>
                        <th>Date</th>
                        <th>Seats Available</th>
                        <th>Host ?</th>
                        <th></th>
                    </tr>
                </thead>
                <tbody>
                    {this.rideList()}
                </tbody>
              </table>
              
          </div>
          
        )
    }
}

MyRidesList.propTypes = {
    auth:PropTypes.object.isRequired,
}

const mapStateToProps = state =>({
    auth:state.auth,
});

export default connect(
    mapStateToProps,
)(MyRidesList);
